import React from "react";
import { useEffect } from "react"; 
import { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Home = () => {
    const [Movies, setMovies] = useState([]);

    useEffect(() => {
        const fetchAllMovies = async () => {
            try {
                const response = await axios.get("http://localhost:8800/Movies");
                setMovies(response.data);
            } catch (err) {
                console.log(err);
            }
        }
        fetchAllMovies();
    },[]);
    console.log(Movies);

    const handleDelete = async (id) => {
        try {
          await axios.delete(`http://localhost:8800/Movies/${id}`);
          window.location.reload()
        } catch (err) {
          console.log(err);
        }
      };

return(
    <div>
        <h1>Home_Page</h1>
        <div className="Movies"> 
        {Movies.map((Movie) => (
            <div className="Movie" 
            key={Movie.id}>
            {Movies.ThumnailLink &&<img src={Movies.ThumnailLink} alt=""/>}
            <h2>{Movies.MovieName}</h2>
            <p>{Movies.Description}</p>
            <span>{Movies.Year}</span>
            <button className="delete" onClick={() => handleDelete(Movie.id)}>Delete</button>
            <button className="update">
              <Link
                to={`/update/${Movie.id}`}
                style={{ color: "inherit", textDecoration: "none" }}>
                Update
              </Link>
            </button>
        </div>
        ))}
        </div>
        <button className="addHom">
            <Link to="/add" style={{ color: "inherit", textDecoration: "none" }}>Add a Movie</Link>
            </button>
        </div>
);
};

export default Home;
